generateRandomUKPostcode = function (delimeter) {
    const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    const numbers = '0123456789';

    // Randomly select two letters for the area code
    const areaCode = letters.charAt(Math.floor(Math.random() * letters.length)) +
        letters.charAt(Math.floor(Math.random() * letters.length));

    // Randomly select one or two digits for the district number
    const districtNumber = numbers.charAt(Math.floor(Math.random() * numbers.length)) +
        (Math.random() > 0.5 ? numbers.charAt(Math.floor(Math.random() * numbers.length)) : '');

    // Randomly select a space or no space (to account for both formats)
    const space = delimeter ?? ' ';

    // Randomly select one number for the sector
    const sector = numbers.charAt(Math.floor(Math.random() * numbers.length));

    // Randomly select two letters for the unit code
    const unitCode = letters.charAt(Math.floor(Math.random() * letters.length)) +
        letters.charAt(Math.floor(Math.random() * letters.length));

    // Combine all parts to form the postcode
    return areaCode + districtNumber + space + sector + unitCode;
}

